INSERT INTO `micro_class` VALUES (1033, 'http://qnm2.cnweike.cn/content/0/2/44/142558/261179.m.mp4', '议论文拟题技术：“1+1”模式', 'http://ziyuan.cnweike.cn/public/images/classroom/default_head_t.png', '梁海媛', '老师', '中国广东茂名市', '信宜市第三中学', '本人是语文中学一级教师，有多年高中语文任教经验，正在潜心研究“搭建高考与高效语文课堂的桥梁”的课题，期待与你携手共进！', '2020-10-28 09:42:00', '高中语文|三.写作/3.议论文', '无', '解题', '普通（基本运用）', '学会议论文拟题技术“1+1”模式，即：核心事件+观点态度。并学习拟题“高阶”技术——“修辞”拟题，运用比喻、化用、借代、对比等修辞手法来拟题，为标题增添光彩。', 0, 0, 'http://qnm2.cnweike.cn/content/0/2/44/142558/261179.m.jpg', 0, 1, 'pass', NULL, NULL);
