INSERT INTO `micro_class` VALUES (1265, 'http://qnm2.cnweike.cn/content/0/2/36/140399/254820.m.mp4', '认识逗号、句号、省略号', 'http://dasai.cnweike.cn/content/0/0/0/251/254819.png', '彭媚', '老师', '中国广东汕尾市', '海丰县鹅埠镇中心小学', '我是小学语文一级教师，彭媚，2014年海丰县小学语文学科优质课评选活动中执教的《一夜的工作》，荣获县一等奖；小学语文录像课《凡卡》2016年全县中小学学科优质课例被评为一等奖。撰写论文先后有20篇在市县级举办的论文大赛中获奖。', '2020-01-19 22:51:49', '小学语文|五.标点符号/2.标号/D.省略号……|五.标点符号/1.点号/A.句号“。”|五.标点符号/1.点号/D.逗号“，”', '无', '讲授', '简单（入门学习）', '让一年级的孩子们初步认识逗号、句号和省略号三种标点符号。', 0, 0, 'http://qnm2.cnweike.cn/content/0/2/36/140399/254820.m.jpg', 0, 1, 'pass', NULL, NULL);
