INSERT INTO `micro_class` VALUES (1512, 'http://qnm2.cnweike.cn/content/0/2/33/139757/253062.m.mp4', '过滤实验操作演示', 'http://ziyuan.cnweike.cn/public/images/classroom/default_head_t.png', '陈星宇', '老师', '中国四川成都市', '蚕丛路小学', '小学初级教师\n区二等奖', '2019-10-29 08:47:10', '无', '小学科学|教科版/四年级上册/第二单元溶解/1.水能溶解一些物质', '实验', '简单（入门学习）', '这是小学生第一次接触过滤实验，在学生进行操作前，教师要进行适当的讲解演示，特别是折装滤纸的方法要示范。而一般用铁架台进行操作过滤时，由于铁架台笨重，占地面积大等原因，实验不方便小学生进行操作，因此设计了简易过滤装置。', 0, 0, 'http://qnm2.cnweike.cn/content/0/2/33/139757/253062.m.jpg', 0, 1, 'pass', NULL, NULL);
