INSERT INTO `micro_class` VALUES (1635, 'http://qnm2.cnweike.cn/content/0/2/32/139265/251751.m.mp4', '城市化过程对自然地理环境的影响', 'http://ziyuan.cnweike.cn/public/images/classroom/default_head_t.png', '曹珂', '老师', '中国湖南益阳市', '益阳十七中', '中学二级教师', '2019-09-20 23:39:44', '无', '高中地理|湘教版/必修二/第二章城市与环境/第三节城市化过程对地理环境的影响', '讲授', '简单（入门学习）', '微课作品采用动画片的形式，以唐僧师徒四人途经一座城市为故事情节，把城市化过程对大气圈、水圈、生物圈、岩石圈的影响这一系列知识点穿插在师徒四人一路上在城里的所见所闻所感中展开。', 0, 0, 'http://qnm2.cnweike.cn/content/0/2/32/139265/251751.m.jpg', 0, 1, 'pass', NULL, NULL);
