INSERT INTO `micro_class` VALUES (1685, 'http://qnm2.cnweike.cn/content/0/2/31/139225/251642.m.mp4', '《问号的用法》', 'http://ziyuan.cnweike.cn/public/images/classroom/default_head_t.png', '陈利红', '老师', '中国四川凉山彝族自治州', '交际河中学', '我是一名从教十四年的乡村语文教师，我现在的职称是一级教师。撰写多篇论论文，并获得了相应的名次。', '2019-09-17 16:49:52', '初中语文|一.字词、语法、常识/4.语文常识', '无', '讲授', '普通（基本运用）', '在语文教学中，我发现许的学生乱用问号，同时问号的用法又是我们考试的考点。通过对问号用法的归类，我总结了问号的七种用来帮助学生更好的掌握和运用问号。', 0, 0, 'http://qnm2.cnweike.cn/content/0/2/31/139225/251642.m.jpg', 0, 1, 'pass', NULL, NULL);
