INSERT INTO `micro_class` VALUES (1698, 'http://qnm2.cnweike.cn/content/0/2/31/139232/251660.m.mp4', '比多少', 'http://ziyuan.cnweike.cn/public/images/classroom/default_head_t.png', '陈学燕', '老师', '中国四川凉山彝族自治州', '布拖县火烈乡中心校', '布拖县火烈乡中心校教师。', '2019-09-18 18:01:53', '小学数学|一.数与代数/1.数的认识/比多少', '无', '讲授', '普通（基本运用）', '本微课的内容是人教版数学一年级上册第一单元第2课时《比多少》。在教学过程中，复习了数数，引导学生认识一一对应的比较方法，并学会用一一对应的方法比较多少，明白多、少的含义。', 0, 0, 'http://qnm2.cnweike.cn/content/0/2/31/139232/251660.m.jpg', 0, 1, 'pass', NULL, NULL);
